std::string getFilename( std::string filename )
{
    int dot   = filename.find_last_of( "." );
    int slash = filename.find_last_of( "/" ) + 1;
    filename = filename.substr( slash, dot - slash );
    for ( auto &c: filename ) c = toupper( c );
    return "IMG_" + filename;
}


